import { state } from '@angular/animations';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Route, Router } from '@angular/router';
import { Country } from 'src/app/common/country';
import { Order } from 'src/app/common/order';
import { OrderItem } from 'src/app/common/order-item';
import { Purchase } from 'src/app/common/purchase';
import { State } from 'src/app/common/state';
import { CartService } from 'src/app/services/cart.service';
import { CheckoutService } from 'src/app/services/checkout.service';
import { Luv2ShopFormService } from 'src/app/services/luv2-shop-form.service';
import { Luv2ShopValidators } from 'src/app/validators/luv2-shop-validators';

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent implements OnInit {


  checkoutFormGroup: FormGroup;

  totalPrice:number=0;
  totalQuantity:number=0;
  creditCardYears:number[]=[];
  creditCardMonths:number[]=[];
  countries:Country[]=[];
  shippingAddressStates:State[]=[];
  billingAddressStates:State[]=[];
  cdr: any;

  constructor(private formBuilder: FormBuilder,private luv2ShopFormService:Luv2ShopFormService,private cartService:CartService,
    private checkoutService:CheckoutService,private router:Router
  ) {
    // Initialize checkoutFormGroup in the constructor
    this.checkoutFormGroup = this.formBuilder.group({
      customer: this.formBuilder.group({
        firstName:new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace]),
        lastName: new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace]),
        email: new FormControl("",[Validators.required,Validators.email])
      }),
      shippingAddress: this.formBuilder.group({
        street: new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace]),
        city: new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace]),
        state:new FormControl("",[Validators.required]),
        country:new FormControl("",[Validators.required]),
        zipCode: new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace])
      }),
      billingAddress: this.formBuilder.group({
        street: new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace]),
        city:new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace]),
        state: new FormControl("",[Validators.required]),
        country: new FormControl("",[Validators.required]),
        zipCode: new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace])
      }),
      creditCard: this.formBuilder.group({
        cardType:new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace]),
        nameOnCard:new FormControl("",[Validators.required,Validators.minLength(2),Luv2ShopValidators.notOnlyWhitespace]),
        cardNumber: new FormControl("",[Validators.required,Validators.pattern('[0-9]{16}')]),
        securityCode: new FormControl("",[Validators.required,Validators.pattern('[0-9]{3}')]),
        expirationMonth: new FormControl("",[Validators.required]),
        expirationYear:  new FormControl("",[Validators.required])
      })
    });
  }

  get firstName(){
    return this.checkoutFormGroup.get("customer.firstName");
  }
  get lastName(){
    return this.checkoutFormGroup.get("customer.lastName");
  }
  get email(){
    return this.checkoutFormGroup.get("customer.email");
  }

  get shippingAddressStreet(){
    return this.checkoutFormGroup.get('shippingAddress.street');
  }

  get shippingAddressCity(){
    return this.checkoutFormGroup.get('shippingAddress.city');
  }
  get shippingAddressState(){
    return this.checkoutFormGroup.get('shippingAddress.state');
  }

  get shippingAddressCountry(){
    return this.checkoutFormGroup.get('shippingAddress.country');
  }

  get shippingAddressZipCode(){
    return this.checkoutFormGroup.get('shippingAddress.zipCode');
  }

  get billingAddressStreet(){
    return this.checkoutFormGroup.get('billingAddress.street');
  }

  get billingAddressCity(){
    return this.checkoutFormGroup.get('billingAddress.city');
  }
  get billingAddressState(){
    return this.checkoutFormGroup.get('billingAddress.state');
  }

  get billingAddressCountry(){
    return this.checkoutFormGroup.get('billingAddress.country');
  }

  get billingAddressZipCode(){
    return this.checkoutFormGroup.get('billingAddress.zipCode');
  }

  get creditCardType(){return this.checkoutFormGroup.get('creditCard.cardType');}

  get creditCardNameOnCard(){return this.checkoutFormGroup.get('creditCard.nameOnCard');}

  get creditCardNumber(){return this.checkoutFormGroup.get('creditCard.cardNumber');}

  get creditCardSecurityCode(){return this.checkoutFormGroup.get('creditCard.securityCode');}

  get creditCardExpirationMonth(){return this.checkoutFormGroup.get('creditCard.expirationMonth');}

  get creditCardExpirationYear(){return this.checkoutFormGroup.get('creditCard.expirationYear');}

  ngOnInit(): void {
    const startMonth:number=new Date().getMonth()+1;
    console.log("start MOnth"+startMonth);
    this.luv2ShopFormService.getCreditCardMonths(startMonth).subscribe(data=>
    {
      console.log(JSON.stringify(data));
      this.creditCardMonths=data;
    }
    );
    this.luv2ShopFormService.getCreditCardYears().subscribe(data=>
      {
        console.log(JSON.stringify(data));
        this.creditCardYears=data;
      }
      );
      this.luv2ShopFormService.getCountries().subscribe(data=>
      {
        console.log(JSON.stringify(data));
        this.countries=data;
      }
      );
      this.reviewCartDetails();
  }
  reviewCartDetails() {
    this.cartService.totalQuantity.subscribe(totalQuantity=>this.totalQuantity=totalQuantity);
    this.cartService.totalPrice.subscribe(totalPrice=>this.totalPrice=totalPrice);
  }

  copyShippingAddressToBillingAddress(event: Event): void {
    const isChecked = (event.target as HTMLInputElement).checked;
    if (isChecked) {
      this.checkoutFormGroup.controls['billingAddress'].patchValue(
        this.checkoutFormGroup.controls['shippingAddress'].value
      );
      this.billingAddressStates = [...this.shippingAddressStates];
    } else {
      this.checkoutFormGroup.controls['billingAddress'].reset();
      this.billingAddressStates = [];
    }

    // Trigger change detection to update the view
    this.cdr.detectChanges();
  }



  onSubmit(): void {
    if(this.checkoutFormGroup.invalid){
      this.checkoutFormGroup.markAllAsTouched();
      return ;
    }
    let order=new Order();

    order.totalPrice=this.totalPrice;
    order.totalQuantity=this.totalQuantity;

    const cartItems=this.cartService.cartItems;

    let orderItems:OrderItem[]=[];
    for(let i=0;i<cartItems.length;i++){
      orderItems[i]=new OrderItem(cartItems[i]);
    }

    let purchase=new Purchase();

    purchase.customer=this.checkoutFormGroup.controls['customer'].value;

    purchase.shippingAddress=this.checkoutFormGroup.controls['shippingAddress'].value;
    const shippingState:State=JSON.parse(JSON.stringify(purchase.shippingAddress.state));
    const shippingCountry:Country=JSON.parse(JSON.stringify(purchase.shippingAddress.country));
    purchase.shippingAddress.state=shippingState.name;
    purchase.shippingAddress.country=shippingCountry.name;

    purchase.shippingAddress=this.checkoutFormGroup.controls['billingAddress'].value;
    const billingState:State=JSON.parse(JSON.stringify(purchase.shippingAddress.state));
    const billingCountry:Country=JSON.parse(JSON.stringify(purchase.billingAddress.country));
    purchase.billingAddress.state=billingState.name;
    purchase.billingAddress.country=billingCountry.name;

    purchase.order=order;
    purchase.orderItems=orderItems;

    this.checkoutService.placeOrder(purchase).subscribe(
      {
        next:response=>{
          alert(`Your order has been recieved with traking number :${response.orderTrackingNumber}`);
          this.resetCart();
        },
        error:err=>{
          alert(`There was an error:${err.message}`);
        }
      }
    )
  }
  resetCart() {
    this.cartService.cartItems=[];
    this.cartService.totalPrice.next(0);
    this.cartService.totalQuantity.next(0);
    this.router.navigateByUrl("/products");
  }

  handleMonthsAndYears() {
    const creditCardFormGroup=this.checkoutFormGroup.get("creditCard");
    const currentYear:number=new Date().getFullYear();
    const selectedYear:number=Number(creditCardFormGroup?.value.expirationYear);
    let startMonth:number;
    if(currentYear==selectedYear){
      startMonth=new Date().getMonth()+1;
    }
    else{
      startMonth=1;
    }
    this.luv2ShopFormService.getCreditCardMonths(startMonth).subscribe(data=>
    {
      console.log(JSON.stringify(data));
      this.creditCardMonths=data;
    }
  );
    }
    getStates(formGroupName: string) {
      const formGroup=this.checkoutFormGroup.get(formGroupName);
      const countryCode=formGroup?.value.country.code;
      this.luv2ShopFormService.getStates(countryCode).subscribe(
        data=>{
          if(formGroupName==="shippingAddress"){
            this.shippingAddressStates=data;
          }
          else{
            this.billingAddressStates=data;
          }
          formGroup?.get('state')?.setValue(data[0]);
        }
      );
      }


}
