import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CartItem } from 'src/app/common/cart-item';
import { Product } from 'src/app/common/product';
import { CartService } from 'src/app/services/cart.service';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list-grid.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {



  products: Product[] = [];
  currentCategoryId: number = 0;
  previousCategoryId: number = 1;
  searchMode: boolean = false;
  thePageNumber: number = 1;
  thePageSize: number = 10;
  theTotalElements: number = 0;
  previousKeyword: string = "";

  constructor(private productService: ProductService,
              private route: ActivatedRoute,
            private cartService:CartService) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe(() => {
      this.listProducts();
    });
  }

  listProducts() {
    this.searchMode = this.route.snapshot.paramMap.has('keyword');
    if (this.searchMode) {
      this.handleSearchProducts();
    } else {
      this.handleListProducts();
    }
  }

  handleSearchProducts() {
    const theKeyword: string = this.route.snapshot.paramMap.get('keyword')!;
    if (this.previousKeyword !== theKeyword) {
      this.thePageNumber = 1;
    }
    this.previousKeyword = theKeyword;

    this.productService.searchProductPaginate(this.thePageNumber - 1, this.thePageSize, +theKeyword)
      .subscribe(this.processResult());
  }

  processResult() {
    return (data: any) => {
      this.products = data._embedded.products;
      this.thePageNumber = data.page.number + 1;
      this.thePageSize = data.page.size;
      this.theTotalElements = data.page.totalElements;
    };
  }

  updatePageSize(pageSize: number) {  // Now receives the correct number type from the template
    this.thePageSize = pageSize;
    this.thePageNumber = 1;
    this.listProducts();
  }

  handleListProducts() {
    const hasCategoryId: boolean = this.route.snapshot.paramMap.has('id');

    if (hasCategoryId) {
      this.currentCategoryId = +this.route.snapshot.paramMap.get('id')!;
    } else {
      this.currentCategoryId = 1;
    }

    if (this.previousCategoryId !== this.currentCategoryId) {
      this.thePageNumber = 1;
    }

    this.previousCategoryId = this.currentCategoryId;

    this.productService.getProductListPaginate(this.thePageNumber - 1, this.thePageSize, this.currentCategoryId)
      .subscribe(this.processResult());
  }
  addToCart(theProduct: Product) {
    console.log(`Adding to cart:${theProduct.name},${theProduct.unitPrice}`);
    const theCartItem=new CartItem(theProduct);
    this.cartService.addToCart(theCartItem);
  }
}
<div class="main-content">
    <div class="section-content section-content-p30">
        <div class="container-fluid">
            <div class="row">
                <!-- Loop through products -->
                <div *ngFor="let tempProduct of products" class="col-md-3">
                    <div class="product-box">
                        <!-- Product link with routerLink -->
                        <a [routerLink]="'/products/' + tempProduct.id">
                            <img [src]="tempProduct.imageUrl" class="img-responsive" alt="{{ tempProduct.name }}">
                        </a>
                        <!-- Product name link -->
                        <a [routerLink]="'/products/' + tempProduct.id">
                            <h1>{{ tempProduct.name }}</h1>
                        </a>
                        <!-- Product price -->
                        <div class="price">
                            {{ tempProduct.unitPrice | currency: 'USD' }}
                            <button (click)="addToCart(tempProduct)" class="btn btn-primary">Add to cart</button>
                        </div>
                    </div>
                </div>

                <!-- No Products Found alert -->
                <div *ngIf="products?.length == 0" class="alert alert-warning col-md-3" role="alert">
                    No Product Found!!
                </div>
            </div>

            <!-- Pagination and Page Size -->
            <div class="footer-pagination">
                <div class="row">
                    <div class="col-md-6">
                        <!-- Empty space or other content -->
                    </div>
                    <div class="col-md-6">
                        <div class="row">
                            <!-- Pagination Component -->
                            <div class="col-md-9" style="padding-left: 30%;">
                                <ngb-pagination 
                                    [(page)]="thePageNumber"
                                    [pageSize]="thePageSize"
                                    [collectionSize]="theTotalElements"
                                    (pageChange)="listProducts()">
                                </ngb-pagination>
                            </div>

                            <!-- Page Size Dropdown -->
                            <div class="col-md-3 mt-2" style="text-align: right;">
                                <span class="mr-2">Page size</span>
                                <select #myPageSelect (change)="updatePageSize(+myPageSelect.value)">
                                    <option [value]="5">5</option>
                                    <option [value]="10">10</option>
                                    <option [value]="20">20</option>
                                </select>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
