import { Injectable } from '@angular/core';
import { CartItem } from '../common/cart-item';
import { Subject } from 'rxjs';
import { template } from '@babel/core';

@Injectable({
  providedIn: 'root'
})
export class CartService {
  cartItems:CartItem[]=[];
  totalPrice:Subject<number>=new Subject<number>();
  totalQuantity:Subject<number>=new Subject<number>();
  constructor() { }
  addToCart(theCartItem:CartItem){
    let alreadyExistsInCart:boolean=false;
    let existingCartItem: CartItem | undefined = undefined;
    if(this.cartItems.length>0){
      for(let temCartItem of this.cartItems){
        if(temCartItem.id==theCartItem.id){
          existingCartItem=temCartItem;
          console.log("hello");
          break;
        }
      }
      alreadyExistsInCart=(existingCartItem!=undefined);
      if(alreadyExistsInCart){
        existingCartItem!.quantity++;
      }
      else{
        this.cartItems.push(theCartItem);
        console.log('i am here');
      }

    }
    this.computeCartTotals();

  }
  computeCartTotals() {
    let totalPriceValue:number=0;
    let totalQuantityValue:number=0;
    for(let currentCartItem of this.cartItems){
      totalPriceValue+=currentCartItem.quantity*currentCartItem.unitPrice;
      totalQuantityValue+=currentCartItem.quantity;
    }
    this.totalPrice.next(totalPriceValue);
    this.totalQuantity.next(totalQuantityValue);
    this.logCartData(totalPriceValue,totalQuantityValue);
  }
  logCartData(totalPriceValue: number, totalQuantityValue: number) {
   console.log("contents of the cart");
   for(let temCartItem of this.cartItems){
    const subTotalPrice=temCartItem.quantity*temCartItem.unitPrice;
    console.log(totalPriceValue+" "+totalQuantityValue+" "+subTotalPrice);
   }
   console.log(totalPriceValue.toFixed(2));
  }

}
