import { Component, OnInit } from '@angular/core';
import { CartItem } from 'src/app/common/cart-item';
import { CartService } from 'src/app/services/cart.service';

@Component({
  selector: 'app-cart-details',
  templateUrl: './cart-details.component.html',
  styleUrls: ['./cart-details.component.css']
})
export class CartDetailsComponent implements OnInit {
decrementQuantity(theCartItem: CartItem) {
this.cartService.decrementQuantity(theCartItem);
}
incrementQuantity(theCartItem: CartItem) {

  this.cartService.addToCart(theCartItem);
}

  cartItems:CartItem[]=[];
  totalPrice:number=0;
  totalQuantity:number=0;

  constructor(private cartService:CartService) { }

  ngOnInit(): void {
    this.listCartDetails();
  }
  listCartDetails() {
    this.cartItems=this.cartService.cartItems;
    this.cartService.totalPrice.subscribe(data=>this.totalPrice=data);
    this.cartService.totalQuantity.subscribe(data=>this.totalQuantity=data);
    this.cartService.computeCartTotals();
  }

}
<div class="main-content">
    <div class="section-content section-content-p30">
        <div class="container-fluid">
            <div *ngIf="cartItems.length>0">
            <table class="table table-bordered">
                <tr>
                    <th width="20">Product Image</th>
                    <th width="20">Product detail</th>
                    <th width="20"></th>
                </tr>
                <tr *ngFor="let tempCartItem of cartItems">
                    <td>
                        <img src="{{tempCartItem.imageUrl}}" class="img-responsive" width="150px">
                    </td>
                    <td>
                        <p>{{tempCartItem.name}}</p>
                        <p>
                            {{tempCartItem.unitPrice|currency:"USD"}}
                        </p>
                    </td>
                    <td>
                        <div class="items">
                            <label>Quantitiy:</label>
                            <div class="row no-glutters">
                                <div class="col">
                                    <button class="btn btn-primary btn-sm" (click)="incrementQuantity(tempCartItem)">
                                        <i class="fas fa-plus"></i>
                                    </button>
                                </div>
                                <div class="col ml-4 mr-2">
                                    {{tempCartItem.quantity}}
                                </div>
                                <div class="col">
                                    <button class="btn btn-primary btn-sm" (click)="decrementQuantity(tempCartItem)">
                                        <i class="fas fa-minus"></i>
                                    </button>
                                </div>
                                <div class="col-8"></div>
                            </div>
                        </div>
                        <p class="mt-2">Subtotal:{{tempCartItem.quantity*tempCartItem.unitPrice|currency:"USD"}}</p>
                          </td>

                </tr>
                <tr>
                    <td colspan="2">

                    </td>
                    <td style="font-weight:bold">
                        <p>Total quantity:{{totalQuantity}}</p>
                        <p>Shipping:FREE</p>
                        <p>Total price:{{totalPrice|currency:"USD"}}</p>
                    </td>
                </tr>
            </table>
        </div>
        <div *ngIf="cartItems.length==0" class="alert alert-warning col-md-12" role="alert">
            Your shopping cart is empty
        </div>
        </div>
    </div>
</div>
